import React, { useState, useRef } from "react";
import "./App.css";

function padTime(time) {
  return time.toString().padStart(2, "0");
}

export default function App() {
  const [title, setTitle] = useState("Let's Focus");
  const [timeLeft, setTimeLeft] = useState(25 * 60);
  const [enableStart, setEnableStart] = useState(false);
  const [enableStop, setEnableStop] = useState(true);
  const intervalRef = useRef(null);

  function startTimer() {
    setTitle("You're doing great!");
    intervalRef.current = setInterval(() => {
      setTimeLeft((timeLeft) => {
        if (timeLeft > 0) {
          return timeLeft - 1;
        }
        return 0;
      });
    }, 1000);
    setEnableStart(true);
    setEnableStop(false);
  }

  function stopTimer() {
    setTitle("Keep going, you're almost there!");
    clearInterval(intervalRef.current);
    setEnableStart(false);
    setEnableStop(true);
  }

  function resetTimer() {
    setTitle("Let's Focus!");
    setTimeLeft(25 * 60);
    clearInterval(intervalRef.current);
  }

  const minutes = padTime(Math.floor(timeLeft / 60));
  const seconds = padTime(timeLeft - minutes * 60);

  return (
    <div className="app">
      <h2>{title}</h2>

      <div className="timer">
        <span>{minutes}</span>
        <span>:</span>
        <span>{seconds}</span>
      </div>

      <div className="buttons">
        <button disabled={enableStart} onClick={startTimer}>
          Start
        </button>
        <button disabled={enableStop} onClick={stopTimer}>
          Stop
        </button>
        <button onClick={resetTimer}>Reset</button>
      </div>
    </div>
  );
}
